(function() {
  var ready;

  ready = function() {
    var overImageVisible;
    overImageVisible = false;
    $('body').on('mouseover', '.image_block', function() {
      var overImage;
      if ($('#profile_image').attr('alt') !== "Medium default") {
        if (overImageVisible === true) {
          overImage = $(this).children('.over_image');
          overImage.stop(true);
          overImage.css('opacity', 1);
          overImageVisible = false;
        }
        $(this).children('.over_image').fadeIn();
        return overImageVisible = true;
      }
    });
    $('body').on('mouseleave', '.image_block', function() {
      return $(this).children('.over_image').delay(1000).fadeOut(1000, function() {
        return overImageVisible = false;
      });
    });
    $('#image_comments').perfectScrollbar({
      wheelSpeed: 10
    });
    if ($('.show_image').length !== 0) {
      $('body').css('background-color', '#111');
    }
    $('.file-upload').each(function() {
      if (this.id === 'new_image') {
        return $(this).fileupload({
          dropZone: $(this),
          formData: {
            'member_id': $('#image_member_id').val()
          },
          dataType: "script",
          replaceFileInput: false,
          add: function(e, data) {
            var file, types;
            $('#image_upload_submit').attr('disabled', false);
            types = /(\.|\/)(gif|jpe?g|png)$/i;
            file = data.files[0];
            if (types.test(file.type) || types.test(file.name)) {
              return $('#image_upload_submit').click(function() {
                event.preventDefault();
                $('#progress_bar_section').show();
                data.submit();
                return $('#image_upload_submit').attr('disabled', true);
              });
            } else {
              return alert("" + file.name + " is not a gif, jpeg, or png image file");
            }
          },
          progressall: function(e, data) {
            var progress;
            progress = parseInt(data.loaded / data.total * 100, 10);
            $('#bar').css('width', progress + '%');
            if (progress === 100) {
              $('#progress_bar_section').append("<span id='upload_complete'>Upload complete!</span>");
              return $('#progress_bar_section').delay(5000).fadeOut('slow', function() {
                return $('#upload_complete').remove();
              });
            }
          }
        });
      } else if (this.id === 'hidden_profile_image') {
        return $(this).fileupload({
          type: "PATCH",
          dropZone: $(this).parent(),
          formData: {
            'member_id': $('#image_member_id').val()
          },
          dataType: "script",
          replaceFileInput: false,
          add: function(e, data) {
            var file, types;
            types = /(\.|\/)(gif|jpe?g|png)$/i;
            file = data.files[0];
            if (types.test(file.type) || types.test(file.name)) {
              $('#progress_bar_section').show();
              return data.submit();
            } else {
              return alert("" + file.name + " is not a gif, jpeg, or png image file");
            }
          },
          progressall: function(e, data) {
            var progress;
            progress = parseInt(data.loaded / data.total * 100, 10);
            $('#bar').css('width', progress + '%');
            if (progress === 100) {
              $('#progress_bar_section').append("<span id='upload_complete'>Upload complete!</span>");
              return $('#progress_bar_section').delay(5000).fadeOut('slow', function() {
                return $('#upload_complete').remove();
              });
            }
          }
        });
      } else if (this.id === 'hidden_new_image') {
        return $(this).fileupload({
          dropZone: $(this).parent(),
          formData: {
            'member_id': $('#image_member_id').val()
          },
          dataType: "script",
          replaceFileInput: false,
          add: function(e, data) {
            var file, types;
            types = /(\.|\/)(gif|jpe?g|png)$/i;
            file = data.files[0];
            if (types.test(file.type) || types.test(file.name)) {
              $('#progress_bar_section').fadeTo(200, 0.8);
              return data.submit();
            } else {
              return alert("" + file.name + " is not a gif, jpeg, or png image file");
            }
          },
          progressall: function(e, data) {
            var progress;
            progress = parseInt(data.loaded / data.total * 100, 10);
            $('#bar').css('width', progress + '%');
            if (progress === 100) {
              $('#progress_bar_section').append("<span id='upload_complete'>Upload complete!</span>");
              return $('#progress_bar_section').delay(5000).fadeOut('slow', function() {
                return $('#upload_complete').remove();
              });
            }
          }
        });
      }
    });
    return $(document).bind('drop dragover', function(e) {
      return e.preventDefault();
    });
  };

  $(document).ready(ready);

  $(document).on('page:load', ready);

}).call(this);
